if (flow.get("programming.cmd") === "edit" && flow.get("programming.id") != undefined) {
    flow.set("programming.conflict", "");//reset check context
    for (let i = 0; i < msg.payload.length; i++) {
        if ((flow.get("programming.fromtime") > msg.payload[i].fromtime && flow.get("programming.fromtime") > msg.payload[i].totime && flow.get("programming.totime") > msg.payload[i].fromtime && flow.get("programming.totime") > msg.payload[i].totime) || (flow.get("programming.fromtime") < msg.payload[i].fromtime && flow.get("programming.fromtime") < msg.payload[i].totime && flow.get("programming.totime") < msg.payload[i].fromtime && flow.get("programming.totime") < msg.payload[i].totime)) flow.set("programming.conflict", flow.get("programming.conflict"));//if fromtime or / and totime not overlapping other schedule context conflict remain the same 
        else if (msg.payload[i].id !== flow.get("programming.id")) flow.set("programming.conflict", flow.get("programming.conflict") + msg.payload[i].id + ", ");//exclude schedule selected, if fromtime and / or totime overlapping other schedule add id to conflict context for notify
    }
    if (flow.get("programming.conflict") === "") {
        if (flow.get("programming.fromtime") <= flow.get("programming.totime")) {
            if (global.get("irrigation.irrprgact.id") === flow.get("programming.id")) global.set("irrigation.irrprgact.id", 0); //If the currently active program is changed, irrigation.irrprgact.id is set to zero in order to update the parameters
            if (flow.get("programming.oneshoot") === 0) msg.topic = "UPDATE irrigation SET active = " + flow.get("programming.active") + ", fromtime = " + flow.get("programming.fromtime") + ", totime = " + flow.get("programming.totime") + ", every = " + flow.get("programming.every") + ", duration = " + flow.get("programming.duration") + ", oneshoot = " + flow.get("programming.oneshoot") + " WHERE id = " + flow.get("programming.id") + ";";//if it is a repeated irrigation, it copies the values normally
            let durationsec = (flow.get("programming.duration") / 1000) / (global.get("irrigation.config.flowrate") / 3600);//convert ml of duration in seconds
            if (flow.get("programming.oneshoot") === 1) msg.topic = "UPDATE irrigation SET active = " + flow.get("programming.active") + ", fromtime = " + flow.get("programming.fromtime") + ", totime = " + (flow.get("programming.fromtime") + (durationsec * 1000)) + ", every = 0, duration = " + flow.get("programming.duration") + ", oneshoot = " + flow.get("programming.oneshoot") + " WHERE id = " + flow.get("programming.id") + ";";//otherwise set every to 0 and totime equal to fromtime plus the irrigation duration
            node.send([null, msg]);
            setTimeout(function () {//send second query after 200 ms for not overload sqlite
                msg.topic = "SELECT id, active, fromtime, totime, every, duration, oneshoot FROM irrigation ORDER BY fromtime;";
                node.send([null, msg]);
            }, 200);
        }
        else {
            msg.payload = "From time cannot be greater than To time, the program will not be recorded";
            msg.topic = "Error"
            node.send([msg, null]);
        }
    }
    else {
        msg.payload = "Current schedule overlapping schedule id " + flow.get("programming.conflict");
        msg.topic = "Error"
        node.send([msg, null]);
    }
}
