var millcurrentday = moment().valueOf() - parseInt(moment().startOf('date').format("x")); //subtracts year, month and day from the current date to get only the current hour and minute in milliseconds according with the current time zone
if ((millcurrentday < global.get("irrigation.irrprgact.fromtime") || millcurrentday >= global.get("irrigation.irrprgact.totime")) && global.get("irrigation.irrprgact.id") > 0) global.set("irrigation.irrprgact.id", 0);//if we are out of program time indicates that no irrigation program is active and search for new program otherwise remain on id program and wait every time
if (global.get("irrigation.irrprgact.id") === 0 && global.get("irrigation.manualirrigation") === 0) {//if irrprgact is equal than 0 therefore no program is active and manual irrigation is deactivate
    for (let i = 0; i < msg.payload.length; i++) {
        if (millcurrentday >= msg.payload[i].fromtime && millcurrentday < msg.payload[i].totime && msg.payload[i].active === 1) {//if the current time is greater than or equal to the start time or less than the program end time and is set to active
            node.warn("programma trovato");
            global.set("irrigation.irrprgact.id", msg.payload[i].id);//set the id program as active
            global.set("irrigation.irrprgact.fromtime", msg.payload[i].fromtime);//and copy relative parameter to global context
            global.set("irrigation.irrprgact.duration", msg.payload[i].duration);
            global.set("irrigation.irrprgact.oneshoot", msg.payload[i].oneshoot);
            if (msg.payload[i].oneshoot === 0) {//if the program is not in one shoot mode get the value set
                global.set("irrigation.irrprgact.totime", msg.payload[i].totime);
                global.set("irrigation.irrprgact.every", msg.payload[i].every);
                global.set("irrigation.everytime", millcurrentday - (global.get("irrigation.irrprgact.every") * 60000) );//set every timer to now time minus every time (so the first irrigation start immediatly)
            }
            if (msg.payload[i].oneshoot === 1) {//if the program is in one shoot mode
                global.set("irrigation.irrprgact.totime", (global.get("irrigation.irrprgact.fromtime") + (global.get("irrigation.irrprgact.duration") * 1500)));//set totime, fromtime plus duration *1500 (500 plus for shure remain inside the program)
                global.set("irrigation.irrprgact.every", null);
            }
            return;
        }
    }
}
if (global.get("irrigation.irrprgact.id") > 0 && global.get("irrigation.irrprgact.oneshoot") === 1 && global.get("irrigation.manualirrigation") === 0) {//if irrprgact is greater than 0 therefore a program is active and is a one shoot program and manualirrigation is deactivate
    var msgtopin = {"payload" : null};
    var msgtodb = { "topic": null };
    if (global.get("irrigation.irrisact") === 0){//if irrigation is not active
        global.set("irrigation.irrisact",1);//active the irrigation
        global.set("irrigation.irrtime", millcurrentday);//set the irrigation timer
        if (global.get("irrigation.output.levelirr") === 1) msgtopin.payload = 0;
        if (global.get("irrigation.output.levelirr") === 0) msgtopin.payload = 1;// active relay
        node.send([msgtopin, null]);
        node.warn("avviata irr os");
    }
    if (global.get("irrigation.irrisact") === 1) {
        let durationsec = (global.get("irrigation.irrprgact.duration") / 1000) / (global.get("irrigation.config.flowrate") / 3600);//convert ml of duration in seconds
        if (((millcurrentday - global.get("irrigation.irrtime")) / 1000 ) >= durationsec){//if current millis div 1000 (so in seconds) are greather or equal to duration time
            msgtodb.topic = "INSERT INTO irrigationdone(id, time, duration) VALUES (NULL, " + millcurrentday + ", " + parseInt((global.get("irrigation.config.flowrate") / 3600) * parseInt((millcurrentday - global.get("irrigation.irrtime")) / 1000) * 1000) + ");";//insert in to db irrigation just done in ml integer
            if (global.get("irrigation.output.levelirr") === 1) msgtopin.payload = 1;
            if (global.get("irrigation.output.levelirr") === 0) msgtopin.payload = 0;// deactive relay
            node.send([msgtopin, msgtodb]);
            node.warn("fermata irr os");//notify on debug console
            global.set("irrigation.irrisact", 0);//deactive the irrigation
            global.set("irrigation.irrtime", 0);//reset the irrigation timer
            global.set("irrigation.irrprgact.id", 0);//indicates that no irrigation program is active
        }
    }
    return;
}

